import { useContext } from "react";
import Modal from "../UI/Modal";
import styles from "./Cart.module.css";
import CartContext from "../../store/cart-content";

const Cart = props => {

    const cartCtx = useContext(CartContext);

    const cartItems = <ul className={styles['cart-items']}>{[{ id: 'c1', name: 'Sushi', amount: 2, price: 12.99 }].map(item => <li>{item.name}</li>)}</ul>;


    return (<Modal onCloseCart={props.onCloseCart}>
        {cartItems}
        <div className={styles.total}>
            <span>Total Amount</span>
            <span>35.67</span>
        </div>
        <div className={styles.actions}>
            <button className={styles['button--alt']} onClick={props.onCloseCart}>Close</button>
            <button className={styles.button}>Order</button>
        </div>
    </Modal>);
};

export default Cart;
