    fetch("https://react-hooks-7f7ca-default-rtdb.firebaseio.com/meals")
      .then((response) => {
        return response.json();
      })
      .then((data) => { 
        const mealsList = data.map((meal) => {
            return (
              <MealItem
                id={meal.id}
                key={meal.id}
                name={meal.name}
                description={meal.description}
                price={meal.price}
              />
            );
        } )         
        return mealsList;
      });
  }


  return (
    <section className={styles.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
